import './App.css';
import { useReducer } from "react";
import DigitButton from './DigitButton';

export const ACTIONS = {
  ADD_DIGIT: "add-digit",
  CHOOSE_OPERATION: "choose-operation",
  CLEAR: "clear",
  DELETE_DIGIT: "delete-digit",
  EVALUATE: "evaluate"
}

function reducer(state, { type, payload }) {
  switch (type) {
    case ACTIONS.ADD_DIGIT:
      return {
        ...state,
        currentOperand: `${state.currentOperand || ""}${payload.digit}`
      }
  }

}


function App() {
  const [{ currentOperand, previousOperand, operation }, dispatch] = useReducer(reducer, {});


  return (
    <div id="calculator-grid">
      <div id="display">
        <div id="previous-operand">{previousOperand} {operation}</div>
        <div id="current-operand">{currentOperand}</div>
      </div>
      <button id="clear" className="span-two">AC</button>
      <button id="delete">DEL</button>
      <DigitButton digit="รท" dispatch={dispatch}/>
      <DigitButton digit="1" dispatch={dispatch}/>
      <DigitButton digit="2" dispatch={dispatch}/>
      <DigitButton digit="3" dispatch={dispatch}/>
      <DigitButton digit="4" dispatch={dispatch}/>
      <DigitButton digit="5" dispatch={dispatch}/>
      <DigitButton digit="6" dispatch={dispatch}/>
      <DigitButton digit="7" dispatch={dispatch}/>
      <DigitButton digit="8" dispatch={dispatch}/>
      <DigitButton digit="9" dispatch={dispatch}/>
      <DigitButton digit="0" dispatch={dispatch}/>
      <DigitButton digit="." dispatch={dispatch}/>


      <button id="two">2</button>
      <button id="three">3</button>
      <button id="multiply">*</button>

      <button id="four">4</button>
      <button id="five">5</button>
      <button id="six">6</button>
      <button id="add">+</button>

      <button id="seven">7</button>
      <button id="eight">8</button>
      <button id="nine">9</button>
      <button id="subtract">-</button>

      <button id="decimal">.</button>
      <button id="zero">0</button>
      <button id="equals" className="span-two">=</button>


    </div>
  );
}

export default App;
